import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import logo from '../assets/LogoTidaly.png';
import axios from 'axios';

const instance = axios.create({
    baseURL: 'http://localhost:3333/api/v1',
    headers: { 'Access-Control-Allow-Origin': '*' }
  });

export const Register = (props) => {
    const [email, setEmail] = useState('')
    const [password,  setPass] = useState('')
    const [checkPasswordSame,  setCheckPasswordSame] = useState('')
    const [formValid, setFormValid] = useState(false);
    const [samePassword, setSamePassword] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
          const response = await instance.post("/register", {
            email: email,
            password: password,
          });
          console.log(response);
          window.location.href = "http://localhost:3000/login";
        } catch (error) {
          setErrorMessage(error.response.data.message);
        }
      };


    const checkPasswordIsSame = () => {
        if (password === checkPasswordSame) {
            setSamePassword(true);
        }
    }

    const checkFormValidity = () => {
        if (email && password) {
          setFormValid(false);
        } else {
          setFormValid(false);
        }
      };

    return (
        <div className="auth-form-container">

            <div className="circle-Container">
                <div className="circleRegister">
                    <img className="logo" src={logo} alt="Logo"></img>
                </div>
            </div>

            <h2>Page d'inscription</h2>
            <form className="register-form" onSubmit={handleSubmit}>
                {/* <label htmlFor="name"> Nom de l'utilisateur </label>
                <input className ="BtnRegisterConnexion" value={name} onChange={(e) => {setName(e.target.value); checkFormValidity();}} placeholder="Exemple:Lucas..." id="name" name="name" /> */}

                <label htmlFor="email"> E-mail </label>
                <input className ="BtnRegisterConnexion" value={email} onChange={(e) => {setEmail(e.target.value); checkFormValidity();}} type="email" placeholder="votremail@gmail.com" id="email" name="email" />

                <label htmlFor="password"> Mot de passe </label>
                <input className ="BtnRegisterConnexion" value={password} onChange={(e) => {setPass(e.target.value); checkFormValidity();}} type="password" placeholder="************" id="password" name="password" />

                <label htmlFor="password"> Confirmer le mot de passe </label>
                <input className ="inputClass" value={checkPasswordSame} onChange={(e) => {setCheckPasswordSame(e.target.value); checkFormValidity();}} type="password" placeholder="************" id="password" name="password" />

                <button className="btn-submit" disabled={!formValid} type="submit">S'inscrire</button>
            </form>

            <Link to="/">
                <button className="link-button" >J'ai déjà un compte !</button>
            </Link>
        </div>
    )
}
